# Functions ---------------------------------------------------------------


export_clusters_test <- function(cluster_df, homology_df, long_unitigs_df) {
  cluster_palette <-
    cluster_df %>%
    distinct(cluster) %>%
    mutate(Color = rainbow(n())) %>% 
    mutate(Color = ifelse(is.na(cluster), 'black', Color))
  
  ref <-  
    pafr::read_paf(
      'reference_alignments/T2Tv11_hg002Yv2_chm13/NA18989_T2Tv11_hg002Yv2_chm13_ref-aln.paf',
      tibble = TRUE,
      include_tags = FALSE) %>%
    group_by(qname) %>%
    slice_head(n = 1) %>%
    ungroup() %>%
    dplyr::rename(unitig = qname)
  
  cluster_df %>%
    left_join(cluster_palette, by = 'cluster') %>%
    left_join(homology_df) %>%
    left_join(ref, by='unitig') %>%
    select(unitig, everything()) %>%
    readr::write_csv('test_colors.csv')
  
  invisible(TRUE)
}

